// src/context/AuthContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import { login as apiLogin, registerPatient } from '../api/medicalApi';
import { jwtDecode } from 'jwt-decode'; // Nécessite l'installation : npm install jwt-decode

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

// Initialisation de l'état à partir du stockage local
const getInitialState = () => {
    const token = localStorage.getItem('token');
    if (token) {
        try {
            const decoded = jwtDecode(token);
            // Vérifier l'expiration du token
            if (decoded.exp * 1000 > Date.now()) {
                return {
                    isAuthenticated: true,
                    user: { id: decoded.id, role: decoded.role, email: decoded.email }, // L'email pourrait ne pas être dans le token patient
                    token,
                };
            }
        } catch (error) {
            console.error("Erreur de décodage ou token expiré:", error);
            localStorage.removeItem('token');
        }
    }
    return {
        isAuthenticated: false,
        user: null,
        token: null,
    };
};

export const AuthProvider = ({ children }) => {
    const [authState, setAuthState] = useState(getInitialState);

    // Fonction de connexion unifiée
    const login = async (email, password, role) => {
        try {
            const res = await apiLogin(email, password, role);
            const { token, patient: patientInfo, user: medecinInfo } = res.data;

            const userData = role === 'patient' ? patientInfo : medecinInfo;
            const decoded = jwtDecode(token);

            localStorage.setItem('token', token);
            setAuthState({
                isAuthenticated: true,
                user: {
                    id: decoded.id,
                    role: decoded.role,
                    email: userData.email, // Rétablit l'email depuis la réponse de l'API
                    ...userData
                },
                token,
            });
            return { success: true };

        } catch (error) {
            const message = error.response?.data?.message || 'Échec de l’authentification.';
            return { success: false, message };
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setAuthState({
            isAuthenticated: false,
            user: null,
            token: null,
        });
    };

    // La fonction d'enregistrement peut être passée directement
    const register = async (data) => {
        try {
            const res = await registerPatient(data);
            const { token, patient: patientInfo } = res.data;

            // Simuler la connexion après l'enregistrement
            const decoded = jwtDecode(token);
            localStorage.setItem('token', token);
            setAuthState({
                isAuthenticated: true,
                user: { id: decoded.id, role: decoded.role, ...patientInfo },
                token,
            });
            return { success: true };
        } catch (error) {
            const message = error.response?.data?.message || 'Échec de l’enregistrement.';
            return { success: false, message };
        }
    };

    return (
        <AuthContext.Provider value={{ ...authState, login, logout, register, setAuthState }}>
            {children}
        </AuthContext.Provider>
    );
};
