// src/components/medecin/AddConsultationForm.jsx
import React, { useState } from 'react';
import { addConsultation } from '../../api/medicalApi';

const AddConsultationForm = ({ medecinId, onSuccess, onCancel }) => {
    const [diagnostic, setDiagnostic] = useState('');
    const [notes, setNotes] = useState('');
    const [documentFile, setDocumentFile] = useState(null); // Pour le fichier
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setMessageType('');

        if (!diagnostic) {
            setMessage("Le champ 'Diagnostic' est obligatoire.");
            setMessageType('error');
            return;
        }

        setLoading(true);

        const consultationData = {
            medecin_id: medecinId, // L'ID du médecin est récupéré du contexte utilisateur
            diagnostic,
            notes,
        };

        try {
            // L'API gère le POST multipart/form-data
            await addConsultation(consultationData, documentFile);

            setMessage('Consultation enregistrée avec succès.');
            setMessageType('success');
            setDiagnostic('');
            setNotes('');
            setDocumentFile(null);

            // Notification parent pour rafraîchir les données
            setTimeout(onSuccess, 1500);

        } catch (error) {
            const errorMessage = error.response?.data?.message || "Échec de l'enregistrement de la consultation.";
            setMessage(errorMessage);
            setMessageType('error');
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    const handleFileChange = (e) => {
        // Validation simple du type de fichier (peut être renforcée côté client)
        const file = e.target.files[0];
        if (file && file.type !== 'application/pdf') {
            setMessage("Seuls les fichiers PDF sont autorisés.");
            setMessageType('error');
            setDocumentFile(null);
            e.target.value = null; // Réinitialise l'input
            return;
        }
        setMessage('');
        setDocumentFile(file);
    };

    return (
        <form onSubmit={handleSubmit}>
            {message && <div className={`alert alert-${messageType}`}>{message}</div>}

            <div className="form-group">
                <label htmlFor="diagnostic">Diagnostic Principal *</label>
                <textarea
                    id="diagnostic"
                    className="form-control"
                    rows="3"
                    value={diagnostic}
                    onChange={(e) => setDiagnostic(e.target.value)}
                    required
                ></textarea>
            </div>

            <div className="form-group">
                <label htmlFor="notes">Notes/Prescriptions (optionnel)</label>
                <textarea
                    id="notes"
                    className="form-control"
                    rows="4"
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                ></textarea>
            </div>

            <div className="form-group">
                <label htmlFor="document">Document Associé (PDF - max 5 Mo)</label>
                <input
                    type="file"
                    id="document"
                    accept="application/pdf"
                    onChange={handleFileChange}
                    className="form-control"
                />
                {documentFile && <p style={{ fontSize: '0.9rem', color: 'var(--color-secondary)' }}>Fichier sélectionné : **{documentFile.name}**</p>}
            </div>

            <div style={{ display: 'flex', gap: '10px', marginTop: '20px', justifyContent: 'flex-end' }}>
                <button type="button" className="btn" onClick={onCancel} style={{ backgroundColor: '#CCC', color: 'var(--color-text)' }} disabled={loading}>
                    Annuler
                </button>
                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? 'Enregistrement...' : 'Valider Consultation'}
                </button>
            </div>
        </form>
    );
};

export default AddConsultationForm;
