const Header = () => {
    const { isAuthenticated, user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    // Style interne pour la concision
    const headerStyle = {
        backgroundColor: 'var(--color-primary)',
        padding: '15px 20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        color: 'var(--color-light)',
    };

    const logoStyle = {
        color: 'var(--color-light)',
        textDecoration: 'none',
        fontSize: '1.5rem',
        fontWeight: 'bold',
    };

    const navStyle = {
        display: 'flex',
        gap: '15px',
    };

