// src/pages/Auth/PatientRegister.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const PatientRegister = () => {
    // État pour tous les champs requis dans le backend (authController.registerPatient)
    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        email: '',
        mot_de_passe: '',
        date_naissance: '',
        numero_medical: '',
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { register } = useAuth();
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        // Validation rapide côté client (peut être affinée)
        if (!Object.values(formData).every(val => val.trim())) {
            setError('Veuillez remplir tous les champs obligatoires.');
            setLoading(false);
            return;
        }

        const result = await register(formData);

        setLoading(false);
        if (result.success) {
            // Redirection automatique vers le dashboard après l'enregistrement réussi
            navigate('/patient/dashboard');
        } else {
            setError(result.message);
        }
    };

    return (
        <div className="page-container" style={{ maxWidth: '500px', margin: '30px auto' }}>
            <div className="card">
                <h2>Inscription Patient</h2>
                {error && <div className="alert alert-error">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                        {/* Nom et Prénom */}
                        <div className="form-group">
                            <label htmlFor="nom">Nom</label>
                            <input type="text" id="nom" name="nom" className="form-control" value={formData.nom} onChange={handleChange} required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="prenom">Prénom</label>
                            <input type="text" id="prenom" name="prenom" className="form-control" value={formData.prenom} onChange={handleChange} required />
                        </div>
                    </div>

                    {/* Email et N° Médical */}
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input type="email" id="email" name="email" className="form-control" value={formData.email} onChange={handleChange} required />
                        </div>
                        <div className="form-group">
                            <label htmlFor="numero_medical">N° Médical</label>
                            <input type="text" id="numero_medical" name="numero_medical" className="form-control" value={formData.numero_medical} onChange={handleChange} required />
                        </div>
                    </div>

                    {/* Date de Naissance */}
                    <div className="form-group">
                        <label htmlFor="date_naissance">Date de Naissance</label>
                        <input type="date" id="date_naissance" name="date_naissance" className="form-control" value={formData.date_naissance} onChange={handleChange} />
                    </div>

                    {/* Mot de Passe */}
                    <div className="form-group">
                        <label htmlFor="mot_de_passe">Mot de passe</label>
                        <input type="password" id="mot_de_passe" name="mot_de_passe" className="form-control" value={formData.mot_de_passe} onChange={handleChange} required />
                    </div>

                    <button type="submit" className="btn btn-primary" disabled={loading}>
                        {loading ? 'Enregistrement...' : 'Créer mon Compte'}
                    </button>
                </form>
                <p style={{ marginTop: '1rem', textAlign: 'center' }}>
                    Déjà inscrit ? <Link to="/login/patient" style={{ color: 'var(--color-primary)' }}>Se connecter</Link>
                </p>
            </div>
        </div>
    );
};

export default PatientRegister;
